export default{
  name: "Shop",
  components: {
    BoxProduct,
    BoxCategory,
    // ReviewCostomer
  },
  methods:{


    // ===========filter Product => start================================================
    filterProducts() {
      let dataSend=[this.filterProduct,this.filter]
      // console.log(this.filterProduct);
      // console.log(this.filter);
      this.$store.dispatch('filterProduct',dataSend)
      //  this.$store.dispatch('filterProduct',this.filterProduct,this.filter)
    },
    // ===========filter Product => end================================================


      // ============loader => start=============================================
    loaderToggle: function (show) {
      let loader = document.getElementById("loader");
      if (show) {
        loader.style.visibility = "visible";
      } else {
        loader.style.visibility = "hidden";
      }
    },
    // ============loader => end=============================================
  },
  computed: {
    products () {
      return this.$store.state.products
    },
    catogeries () {
      return this.$store.state.catogery
    },
    sliceCatogery () {
      return this.$store.getters.sliceCatogery
  },
    sliceProducts () {
      return this.$store.getters.sliceProducts
  },
  },
  mounted() {
    this.loaderToggle(true)
    this.$store.dispatch('fetchProduct')
    .then((res) => {
      console.log("sucssed")
      this.loaderToggle(false)
    })
    .catch((error) => {
           this.loaderToggle(false)
           console.log(error);
         });


    this.$store.dispatch('fetchCatogery')
  },
  data(){
    return{
      filter:"title",
      filterProduct:""
    }
  }
}

</script>
